int imx854_retention_enter(struct cam_sensor_ctrl_t *s_ctrl)
{
	int32_t rc = 0;

	CAM_INFO(CAM_SENSOR, "[RET_DBG] E");

	CAM_INFO(CAM_SENSOR, "[RET_DBG] X");

	return rc;
}

